import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios, { AxiosResponse } from "axios";

interface InitialStateType {
	userName: string;
	id: number | null;
	loading: boolean;
}

const initialState: InitialStateType = {
	userName: "",
	id: null,
	loading: false,
};

interface ThunkParams {
	name: string;
}

interface CreateUserResponse extends AxiosResponse {
	data: {
		name: string;
		id: number;
		created_at: string;
		messages: [];
	};
}

export const createUser = createAsyncThunk("chat/user", async ({ name }: ThunkParams) => {
	const backendURL: string = process.env.BACKEND_BASE_URL || "";
	const response = await axios.post(`${backendURL}/chat/user`, {
		name,
	});
	return response as CreateUserResponse;
});

const chatSlice = createSlice({
	name: "chat",
	initialState,
	reducers: {
		updateUserName: (state, action) => {
			state.userName = action.payload;
		},
	},
	extraReducers: (builder) => {
		builder
			.addCase(createUser.fulfilled, (state, { payload }) => {
				state.userName = payload.data.name;
				state.id = payload.data.id;
				state.loading = false;
			})
			.addCase(createUser.rejected, (state, action) => {
				console.error(action.error);
				state.loading = false;
			})
			.addCase(createUser.pending, (state) => {
				state.loading = true;
			});
	},
});

export const { updateUserName } = chatSlice.actions;
export default chatSlice.reducer;
