import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export interface Message {
	content: string;
	user_id: string;
	timestamp?: number;
	id?: number;
	isBot: boolean;
}

interface InitialStateType {
	messages: Message[];
	userInput: string;
	loading: boolean;
}

const initialState: InitialStateType = {
	messages: [],
	userInput: "",
	loading: false,
};

interface SendMessagePayload {
	message: string;
	user_id: number;
}

interface ThunkParams {
	message: string;
}

export const sendMessage = createAsyncThunk("message/send", async ({ message }: ThunkParams) => {
	const backendURL: string = process.env.BACKEND_BASE_URL || "";
	const payload: SendMessagePayload = {
		message: "Hello",
		user_id: 1,
	};
	const response = await axios.post(`${backendURL}/chat/message/new`, {});
	return response;
});

const chatSlice = createSlice({
	name: "chat",
	initialState,
	reducers: {
		addMessage: (state, { payload }) => {
			state.messages.push(payload);
		},
		updateUserInput: (state, action) => {
			state.userInput = action.payload;
		},
		deleteMessage: (state, { payload }) => {
			state.messages = state.messages.filter((msg, index) => index !== payload);
		},
	},
	extraReducers(builder) {
		builder.addCase(sendMessage.fulfilled, (state, { payload }) => {});
	},
});

export const { addMessage, updateUserInput, deleteMessage } = chatSlice.actions;
export default chatSlice.reducer;
