import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { RootState } from "./store";

export interface Message {
	content: string;
	user_id: number;
	timestamp?: number;
	id: number;
	isBot: boolean;
}

interface InitialStateType {
	messages: Message[];
	loading: boolean;
}

const initialState: InitialStateType = {
	messages: [],
	loading: false,
};

interface SendMessageRequest {
	content: string;
	user_id: number;
}

interface SendMessageResponse {
	content: string;
	user_id: number;
	is_bot: boolean;
	id: number;
	created_at: string;
}

export const sendMessage = createAsyncThunk(
	"message/send",
	async (message: string, { getState, rejectWithValue, dispatch }) => {
		try {
			const { user } = getState() as RootState;
			const backendURL: string = process.env.REACT_APP_API_BASE_URL || "";
			const userFromStorage = JSON.parse(localStorage.getItem("user") || "{}");
			if (!user.id && !userFromStorage.id) {
				return rejectWithValue("No User ID");
			}

			const payload: SendMessageRequest = {
				content: message,
				user_id: user.id || userFromStorage.id,
			};

			// Save Message to local state
			dispatch(addMessage({ content: message, user_id: payload.user_id.toString(), isBot: false }));

			const { data } = await axios.post(`${backendURL}/chat/message/new`, payload);

			return data as SendMessageResponse[];
		} catch (error: any) {
			// OPtional way to add errors to the UI (not implemented in this example)
			// if (error.message) {
			// 	dispatch(
			// 		addError({
			// 			message: error.message,
			// 			level: "error",
			// 		})
			// 	);
			// 	dispatch(clearError());
			// }
			return rejectWithValue(error);
		}
	}
);

const updateMessageFromMessages = (messages: Message[], newMessage: SendMessageResponse) => {
	// Going in reverse order to find the last message, but async can mess up the order
	for (let index = messages.length; index > 0; index--) {
		const message = messages[index];

		if (message.content === newMessage.content) {
			messages[index] = {
				content: newMessage.content,
				user_id: newMessage.user_id,
				isBot: newMessage.is_bot,
				id: newMessage.id,
			};
			break;
		}
	}
};

const chatSlice = createSlice({
	name: "chat",
	initialState,
	reducers: {
		addMessage: (state, { payload }) => {
			state.messages.push(payload);
		},
		deleteMessage: (state, { payload }) => {
			state.messages = state.messages.filter((msg, index) => index !== payload);
		},
	},
	extraReducers(builder) {
		builder
			.addCase(sendMessage.fulfilled, (state, { payload }) => {
				const messages = payload as SendMessageResponse[];

				// Update the message with the ID from the server
				if (state.messages.length > 0) {
					console.log(state.messages.length);
					console.log(`Helli ${state.messages[0].content}`);
					updateMessageFromMessages(state.messages, messages[0]);
				} else {
					state.messages.push({
						content: messages[0].content,
						user_id: messages[0].user_id,
						isBot: messages[0].is_bot,
						id: messages[0].id,
					});
				}

				// Add bot response
				state.messages.push({
					content: messages[1].content,
					user_id: messages[1].user_id,
					isBot: messages[1].is_bot,
					id: messages[1].id,
				});

				state.messages.push();
			})
			.addCase(sendMessage.pending, (state) => {
				state.loading = true;
			})
			.addCase(sendMessage.rejected, (state, { payload }) => {
				state.messages.push({
					content: "Sorry, I am not able to respond right now. Please try again later.",
					user_id: state.messages[0].user_id,
					isBot: true,
					id: 999,
				});
				state.loading = false;
			});
	},
});

export const { addMessage, deleteMessage } = chatSlice.actions;
export default chatSlice.reducer;
